import "../styles/Slide.scss";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Autoplay, History, Pagination, Lazy } from "swiper";
import "swiper/swiper.scss";
import "swiper/components/navigation/navigation.scss";
import "swiper/components/pagination/pagination.scss";
import "swiper/components/scrollbar/scrollbar.scss";
import axios from "../api/axios";
import { useEffect, useState } from "react";
import request from "../api/Api";
import { Itoprated } from "../types/fetchdata";

SwiperCore.use([Autoplay, History, Pagination, Lazy]);

const Slide = () => {
  const [toprated, settoprated] = useState<Itoprated[]>([]);

  useEffect(() => {
    axios.get(request.fetchtoprated).then((dat) => {
      settoprated(dat.data.results);
      console.log(dat.data.results);
    });
  }, []);

  return (
    <div className='Slide_all'>
      <Swiper
        pagination={true}
        scrollbar
        style={{ height: "100%" }}
        autoplay={true}
        lazy={true}
        history={true}>
        {toprated.slice(2, 7).map((r) => (
          <SwiperSlide style={{ width: "100%" }}>
            <div className='Slide_inbox'>
              <div className='Image image-fluid'>
                <img
                  src={`https://image.tmdb.org/t/p/original/${r.backdrop_path} `}
                  alt=''
                  className='img-fluid'
                />
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default Slide;
