import "../styles/Navbar.scss";
import React, { RefObject } from 'react';
import { RiNetflixFill } from "react-icons/ri";
import { GrMenu } from "react-icons/gr";
import { ImCancelCircle } from "react-icons/im";
import { useEffect, useState } from "react";
import { RefContext } from '../App';
const Navbar = () => {
  const [small, setSmall] = useState<boolean>(false);
  const { HomeComponentRef } = React.useContext(RefContext);
  const [ref,setRef] = React.useState<RefObject<any> | null>(null);
  const [opentoggle, setopentoggle] = useState<boolean>(false);


  useEffect(() => {
    if (typeof window !== "undefined") {
      window.addEventListener("scroll", () => {
        setSmall(window.pageYOffset > 200);
        setopentoggle(false);
      });
    }
  }, []);

  React.useEffect(() => {
    setRef(context);
  },[context])

  const scroolToHome = (e : any) : void => { 
    e.preventDefault();
    console.log("Ref: " + ref);  
    if (!context){ 
      return;
    }
    context.current.scrollIntoView({ behavior: 'smooth', block: 'start' })
  }

  return (
    <div>
      {opentoggle ? (
        <div className='minsize'>
          <div className='toggles-min'>
            <ul>
              <li onClick={scroolToHome}>Home</li>
              <li>Trends</li>
              <li>Top</li>
              <li>Upcoming</li>
              <li>Latest</li>
            </ul>
          </div>
        </div>
      ) : (
        <div></div>
      )}
      <div className={`Navbar ${small ? "small" : ""}`}>
        <div className='icon'>
          <RiNetflixFill style={{ fontSize: "30px", marginLeft: "20px" }} />
        </div>
        <div className='open-toggle' onClick={() => setopentoggle(!opentoggle)}>
          {opentoggle ? <ImCancelCircle /> : <GrMenu />}{" "}
        </div>
        <div className='toggles'>
          <ul>
            <li onClick={scroolToHome}>Home</li>
            <li>Trends</li>
            <li>Top</li>
            <li>Upcoming</li>
            <li>Latest</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
